import React, { useState } from 'react'
import './Input.css'

export default props => {

    const [valor, setValor] = useState('Inicial')

    function quandoMudar(e){

        setValor(e.target.value)
    }

    return (
        <div className="Input">
            <div style={{
                display: "flex",
                flexDirection: "column"
            }}>
                <h2>{valor}</h2>
                <input value={valor} onChange={quandoMudar}/>
            </div>
        </div>
    )

}
